const CLIENT_ID = '06108a62c9b6496eb46b41193b915681'; // Your client id
const REDIRECT_URI = 'http://localhost:3000/login'; // Your redirect uri

const AUTH = "https://accounts.spotify.com/authorize"
const SCOPES = ["playlist-modify-public", "playlist-modify-private"]
const SPACE_DELIMITER = "%20"
const SCOPE_URL_PARAM = SCOPES.join(SPACE_DELIMITER)

export const handleLogin = () => {
    window.location = `${AUTH}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&scope=${SCOPE_URL_PARAM}&response_type=token&show_dialogue=true`
}
